properties.entry:
CREATE or REPLACE TRIGGER #triggername
	BEFORE INSERT or UPDATE OF #column ON #scheme,#table
	FOR EACH ROW
	DECLARE
	E_RANGEEXCEPTION EXCEPTION;
	BEGIN
	IF :new.#column #operator #value1 AND #value2 THEN
	RAISE E_RANGEEXCEPTION;
	END IF;
	EXCEPTION
	WHEN E_RANGEEXCEPTION THEN
	  RAISE_APPLICATION_ERROR
	        (-20001, 'E_RANGEEXCEPTION - Value cannot be between 
	#value1 and #valuehaaloo1234');
	END;
